modules.original_code:
module half_adder(
    input A,
    input B,
    output sum,
    output carry
);

assign sum = A ^ B;
assign carry = A & B;

endmodule

modules.masked_code:
module half_adder_masked(
    input A0,
    input A1,
    input B0,
    input B1,
    input rN,
    output sum0,
    output sum1,
    output carry0,
    output carry1
);

wire sum_temp0;
wire sum_temp1;
wire carry_temp0;
wire carry_temp1;

assign sum_temp0 = A0 ^ B0;
assign sum_temp1 = A1 ^ B1;

assign sum0 = sum_temp0;
assign sum1 = sum_temp1;

wire p00, p01, p10, p11;

assign p00 = A0 & B0;
assign p01 = A0 & B1;
assign p10 = A1 & B0;
assign p11 = A1 & B1;

assign carry0 = p00 ^ p01 ^ rN;
assign carry1 = p11 ^ p10 ^ rN;

endmodule
