modules.original_code:
module half_adder (
  input A,
  input B,
  output S,
  output Cout
);

assign S = A ^ B;
assign Cout = A & B;

endmodule

modules.masked_code:
module half_adder_masked (
  input A0,
  input A1,
  input B0,
  input B1,
  input rN,
  output S0,
  output S1,
  output Cout0,
  output Cout1
);

assign S0 = A0 ^ B0;
assign S1 = A1 ^ B1;

wire p00, p01, p10, p11;
assign p00 = A0 & B0;
assign p01 = A0 & B1;
assign p10 = A1 & B0;
assign p11 = A1 & B1;

assign Cout0 = p00 ^ p01 ^ rN;
assign Cout1 = p11 ^ p10 ^ rN;

endmodule
