module half_adder_masked (
    input a0,
    input a1,
    input b0,
    input b1,
    input rN,
    output sum0,
    output sum1,
    output carry0,
    output carry1
);

wire sum_temp0;
wire sum_temp1;
wire carry_temp0;
wire carry_temp1;

assign sum_temp0 = a0 ^ b0;
assign sum_temp1 = a1 ^ b1;

assign sum0 = sum_temp0;
assign sum1 = sum_temp1;

wire p00, p01, p10, p11;

assign p00 = a0 & b0;
assign p01 = a0 & b1;
assign p10 = a1 & b0;
assign p11 = a1 & b1;

assign carry0 = p00 ^ p01 ^ rN;
assign carry1 = p11 ^ p10 ^ rN;

endmodule
