modules.masked_code:
module half_adder_masked (
  input A0,
  input A1,
  input B0,
  input B1,
  input rN,
  output S0,
  output S1,
  output C0,
  output C1
);

assign S0 = A0 ^ B0;
assign S1 = A1 ^ B1;

wire p00, p01, p10, p11;
assign p00 = A0 & B0;
assign p01 = A0 & B1;
assign p10 = A1 & B0;
assign p11 = A1 & B1;

assign C0 = p00 ^ p01 ^ rN;
assign C1 = p11 ^ p10 ^ rN;

endmodule
