modules.original_code:
module half_adder (
    input A,
    input B,
    output Sum,
    output Carry
);

assign Sum = A ^ B;
assign Carry = A & B;

endmodule

modules.masked_code:
module half_adder_masked (
    input A0,
    input A1,
    input B0,
    input B1,
    input r0,
    output Sum0,
    output Sum1,
    output Carry0,
    output Carry1
);

wire Sum_temp0;
wire Sum_temp1;
wire Carry_temp0;
wire Carry_temp1;

assign Sum_temp0 = A0 ^ B0;
assign Sum_temp1 = A1 ^ B1;

assign Sum0 = Sum_temp0;
assign Sum1 = Sum_temp1;

wire p00, p01, p10, p11;

assign p00 = A0 & B0;
assign p01 = A0 & B1;
assign p10 = A1 & B0;
assign p11 = A1 & B1;

assign Carry0 = p00 ^ p01 ^ r0;
assign Carry1 = p11 ^ p10 ^ r0;

endmodule
