modules.original_code:
module half_adder (
    input A,
    input B,
    output Sum,
    output Carry_out
);

assign Sum = A ^ B;
assign Carry_out = A & B;

endmodule

modules.masked_code:
module half_adder_masked (
    input A0,
    input A1,
    input B0,
    input B1,
    input r0,
    output Sum0,
    output Sum1,
    output Carry_out0,
    output Carry_out1
);

wire p00, p01, p10, p11;
wire Carry_out0_temp, Carry_out1_temp;

assign Sum0 = A0 ^ B0;
assign Sum1 = A1 ^ B1;

assign p00 = A0 & B0;
assign p01 = A0 & B1;
assign p10 = A1 & B0;
assign p11 = A1 & B1;

assign Carry_out0 = p00 ^ p01 ^ r0;
assign Carry_out1 = p11 ^ p10 ^ r0;

endmodule
